import React, { useState, useEffect, useMemo } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import styled from "styled-components";
import bg from "./img/bg.png";
import { MainLayout } from "./styles/Layouts";
import Orb from "./Components/Orb/Orb";
import Navigation from "./Components/Navigation/Navigation";
import Dashboard from "./Components/Dashboard/Dashboard";
import Income from "./Components/Income/Income";
import Expenses from "./Components/Expenses/Expenses";
import { useGlobalContext } from "./context/globalContext";
import SignUp from "./pages/signup"; // Signup Page
import Login from "./pages/login"; // Login Page
import SplashScreen from "./Components/SplashScreen/splashscreen"; // Splash Screen

function App() {
  const [loading, setLoading] = useState(true);
  const [active, setActive] = useState(1);
  const global = useGlobalContext();
  console.log(global);

  // Show Splash Screen for 3 seconds
  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 3000);
  }, []);

  const displayData = () => {
    switch (active) {
      case 1:
      case 2:
        return <Dashboard />;
      case 3:
        return <Income />;
      case 4:
        return <Expenses />;
      default:
        return <Dashboard />;
    }
  };

  const orbMemo = useMemo(() => <Orb />, []);

  return (
    <Router>
      {loading ? (
        <SplashScreen /> // Show splash screen while loading
      ) : (
        <AppStyled bg={bg} className="App">
          {window.location.pathname !== "/signup" && window.location.pathname !== "/login" && (
            <>
              {orbMemo}
              <MainLayout>
                <Navigation active={active} setActive={setActive} />
                <main>
                  <Routes>
                    <Route path="/" element={displayData()} />
                    <Route path="/signup" element={<SignUp />} />
                    <Route path="/login" element={<Login />} />
                    <Route path="*" element={<Navigate to="/" />} />
                  </Routes>
                </main>
              </MainLayout>
            </>
          )}

          {window.location.pathname === "/signup" && <SignUp />}
          {window.location.pathname === "/login" && <Login />}
        </AppStyled>
      )}
    </Router>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${(props) => props.bg});
  position: relative;
  main {
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #ffffff;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar {
      width: 0;
    }
  }
`;

export default App;
