import React, { useState, useEffect, useMemo } from "react";
import { BrowserRouter as Router, Route, Routes, useLocation } from "react-router-dom";
import styled from "styled-components";
import bg from "./img/bg.png";
import { MainLayout } from "./styles/Layouts";
import Orb from "./Components/Orb/Orb";
import Navigation from "./Components/Navigation/Navigation";
import Dashboard from "./Components/Dashboard/Dashboard";
import Income from "./Components/Income/Income";
import Expenses from "./Components/Expenses/Expenses";
import { useGlobalContext } from "./context/globalContext";
import SignUp from "./pages/signup.js"; 
import Login from "./pages/login.js"; 
import SplashScreen from "./Components/splashscreen/Splashscreen.js"; 

function App() {
  const [loading, setLoading] = useState(true);
  const [active, setActive] = useState(1);

  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 3000);
  }, []);

  return (
    <Router>
      {loading ? (
        <SplashScreen />
      ) : (
        <AppStyled bg={bg} className="App">
          <Routes>
            <Route path="/signup" element={<SignUp />} />
            <Route path="/login" element={<Login />} />
            <Route
              path="/*"
              element={
                <MainApp active={active} setActive={setActive} />
              }
            />
          </Routes>
        </AppStyled>
      )}
    </Router>
  );
}

function MainApp({ active, setActive }) {
  const location = useLocation();

  // Memoize Orb to prevent unnecessary re-renders
  const orbMemo = useMemo(() => <Orb />, []);

  // If on signup or login page, don't render navigation or dashboard
  if (location.pathname === "/signup" || location.pathname === "/login") {
    return null;
  }

  const displayData = () => {
    switch (active) {
      case 1:
      case 2:
        return <Dashboard />;
      case 3:
        return <Income />;
      case 4:
        return <Expenses />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <>
      {orbMemo}
      <MainLayout>
        <Navigation active={active} setActive={setActive} />
        <main>{displayData()}</main>
      </MainLayout>
    </>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${(props) => props.bg});
  position: relative;
  main {
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #ffffff;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar {
      width: 0;
    }
  }
`;

export default App;
